  private void i_wannaActionPerformed(java.awt.event.ActionEvent evt) {
    ChatRoom.createSession(this.good);
    Transaction transaction = new Transaction();
    transaction.setGid(good.getGid());
    transaction.setTprice(good.getPrice());
    transaction.setTimeMillis(System.currentTimeMillis());
    transaction.setSeller_id(good.getOwner());
    transaction.setBuyer_id(Client.getClient().getMe());
    Client.getClient().getTransactionService().createTransaction(transaction);
    Main.sell.setVisible(false);
    Main.home.setVisible(false);
    Main.chatRoom.setVisible(true);
  }


